import React, { useEffect, useState } from 'react'

const ProductList = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = () => {
        let result = fetch("http://localhost:5000/products");
        result = result.json();
        setProducts(result);
    }
    console.warn("products", products);


    return (
        <div className="contain-table">
            <table className="striped-table">
                <thead>
                    <tr>
                        <th>No.</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email</th>
                        <th>Date</th>
                        <th colSpan={2} className="text-center">
                            Actions
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((employee, i) => (
                            <tr key={products.id}>
                                <td>{i + 1}</td>
                                <td>{products.firstName}</td>
                                <td>{products.lastName}</td>
                                <td>{products.email}</td>
                                <td>{products.date} </td>

                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan={7}>No Employees</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    )
}

export default ProductList;
