import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';

const ProductList = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        let result = await fetch("http://localhost:5000/products");
        result = await result.json();
        setProducts(result);
    }
    const deleteProduct = async (id) => {
        let result = await fetch(`http://localhost:5000/product/${id}`, {
            method: "Delete"
        });
        result = await result.json()
        if (result) {
            getProducts();
        }
    }
    const searchHandle = (event) => {
        console.warn(event.target.value);
    }

    return (

        <div className="container-table container mt-5">
            <h1 className='justify-content-center form-outline m-4'> Products List</h1>
            <input type="text" className='input form-control m-4' placeholder='Search Product' onChange={searchHandle} />
            <table className=" table table-hover striped-table">
                <thead style={{ padding: "10px" }}>
                    <tr>
                        <th>S. No</th>
                        <th>Name</th>
                        <th>Prise</th>
                        <th>Category</th>
                        <th>Company</th>
                        <th colSpan={2} className="text-center">
                            Actions
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((item, index) =>
                        <tr key={item._id}>
                            <td>{index + 1}</td>
                            <td>{item.name}</td>
                            <td>{item.price} </td>
                            <td>{item.category} </td>
                            <td>{item.company} </td>
                            <td className="text-right">
                                <Link to={"/update/" + item._id}>
                                    <button className="button muted-button">
                                        Update
                                    </button>
                                </Link>
                            </td>
                            <td className="text-left">
                                <button onClick={() => deleteProduct(item._id)} className="button muted-button">
                                    Delete
                                </button>
                            </td>
                        </tr>
                    )}

                </tbody>
            </table>
        </div>



    );
};

export default ProductList;
